{"blocks":[{"key":"d1dg5","text":"To explore the meta-analysis results for any particular gene in more detail, use the function forest.rma() from the metafor package to make a forest plot illustrating the effect sizes (Log2FC) and confidence intervals for each study.","type":"unstyled","depth":0,"inlineStyleRanges":[{"style":"italic","offset":94,"length":13},{"style":"italic","offset":116,"length":7}],"entityRanges":[],"data":{}},{"key":"5bc3m","text":" ","type":"atomic","depth":0,"inlineStyleRanges":[],"entityRanges":[{"key":0,"offset":0,"length":1}],"data":{}},{"key":"6n41v","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"1dlo7","text":" ","type":"atomic","depth":0,"inlineStyleRanges":[],"entityRanges":[{"key":1,"offset":0,"length":1}],"data":{}},{"key":"asmbj","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"command","mutability":"IMMUTABLE","data":{"can_edit":true,"command_name":"Function: Making forest plots to illustrate differential expression results across studies for a gene","description":"","guid":"7CEE98672A8311EF85500A58A9FEAC02","name":"MakeForestPlots<-function(GeneSymbol){

  pdf(paste("ForestPlot_", GeneSymbol, ".pdf", sep=""), height=5, width=8)

  effect<-as.numeric(MetaAnalysis_FoldChanges_ForMeta[MetaAnalysis_FoldChanges_ForMeta$x==GeneSymbol,-1])
  var<-as.numeric(MetaAnalysis_SV_ForMeta[MetaAnalysis_FoldChanges_ForMeta$x==GeneSymbol,-1])

  forest.rma(rma(effect, var),slab=colnames(MetaAnalysis_FoldChanges_ForMeta)[-1],  xlim=c(-3, 3))

  mtext(paste(GeneSymbol), line=-1.5, cex=2)
  dev.off()
}
