{"blocks":[{"key":"498mc","text":"Select ‘Run’ > ‘Run for project’. Move each scene image over to the ‘Selected’ window and select ‘OK’. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"eueoh","text":" ","type":"atomic","depth":0,"inlineStyleRanges":[],"entityRanges":[{"key":0,"offset":0,"length":1}],"data":{}},{"key":"6lag4","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"code_insert","mutability":"IMMUTABLE","data":{"blocks":[{"data":{},"depth":0,"entityRanges":[],"inlineStyleRanges":[],"key":"7p9rt","text":"import qupath.lib.images.servers.LabeledImageServer 
def imageData = getCurrentImageData() 
// Define output path (relative to project) 
def name = GeneralTools.getNameWithoutExtension(imageData.getServer().getMetadata().getName()) 
def pathOutput = buildFilePath(PROJECT_BASE_DIR, 'export', name) 
mkdirs(pathOutput) 
// Export at full resolution 
double downsample = 1.0 
// Create an ImageServer where the pixels are derived from annotations 
def labelServer = new LabeledImageServer.Builder(imageData) 
 .backgroundLabel(0, ColorTools.WHITE) // Specify background label (usually 0 or 255) 
 .downsample(downsample) // Choose server resolution; this should match the resolution at which tiles are exported 
 .addLabel('Slc17a7', 1) // Choose output labels (the order matters!) 
.addLabel(‘Gad1’, 2) 
 .multichannelOutput(false) // If true, each label refers to the channel of a multichannel binary image (required for multiclass 
probability) 
 .build() 
// Export each region 
int i = 0 
for (annotation in getAnnotationObjects()) { 
 name = annotation.getName() 
 if (annotation.getROI().getRoiName() == "Rectangle") { 
 def region = RegionRequest.createInstance( 
 labelServer.getPath(), downsample, annotation.getROI()) 
 i++ 
 def outputPath = buildFilePath(pathOutput, name + '.png') 
 writeImageRegion(labelServer, region, outputPath) 
}} ","type":"unstyled"}],"entityMap":{}}}}}
